    private void txtcalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtcalcularActionPerformed

        double perimetro=Double.parseDouble(txtperimetro.getText());
        double apotema=Double.parseDouble(txtapotema.getText());

        txtarea.setText(String.valueOf(perimetro*apotema));

    }//GEN-LAST:event_txtcalcularActionPerformed

